import styled from 'styled-components'
import { useState } from 'react'

const ButtonStyle = styled.button`
  background-color: ${({ colorValue }) =>
    colorValue !== '' ? `#${colorValue}` : 'white'};
`
const LinkStyle = styled.a`
  text-decoration: none;
  color: ${({ colorValue }) =>
    colorValue !== '' ? `#${colorValue}` : 'white'};
  &:focus,
  &:visited,
  &:link,
  &:active {
    text-decoration: none !important;
    color: ${({ colorValue }) =>
      colorValue !== '' ? `#${colorValue}` : 'white'};
  }
  &: hover {
    text-decoration: underline !important;
    color: 'green' !important;
  }
`

const LinkWrapper = styled.div`
  padding-top: 30px;
  & a:first-of-type {
    margin-right: 20px;
  }
`

function ColoredButton() {
  const [colorValue, setColorValue] = useState('white')
  const [name, setName] = useState('Nice name')
  const [hiddenColorValue, setHiddenColorValue] = useState(false)

  const handleClickBtn = (e) => {
    console.log('change color')
    let randomColor = Math.floor(Math.random() * 16777215).toString(16)

    setColorValue(randomColor)
    setName(e.target.getAttribute('name'))

    setHiddenColorValue(true)
  }

  const handleBlur = (e) => {
    console.log('hide color val')
    setHiddenColorValue(false)
  }

  return (
    <div>
      <ButtonStyle
        onClick={handleClickBtn}
        colorValue={colorValue}
        name={name}
        onBlur={handleBlur}
      >
        Change my color !
      </ButtonStyle>

      {hiddenColorValue && (
        <div>
          <p>My name : {name}</p>
          <p>My color : #{colorValue}</p>
        </div>
      )}
      <LinkWrapper>
        <LinkStyle
          href="https://flora-le.github.io/la-maison-jungle/"
          colorValue={colorValue}
        >
          Plants over here
        </LinkStyle>
        <a href="https://flora-le.github.io/shiny-agency/">Shiny Agency</a>
      </LinkWrapper>
    </div>
  )
}

export default ColoredButton
